// ========================================
// IMPORTS
// ========================================

// CSS
import classes from "./icons.module.css"

// Mantine
import {
	Container,
	HoverCard,
	HoverCardTarget,
	HoverCardDropdown,
	Text,
} from "@mantine/core"

// Shared components
import { TransitionLink } from "@/components/shared"

// ========================================
// ICON WRAPPER
// ========================================

// Higher-order component to wrap the SVG in a Container with styling
const iconWrapper = (Icon: React.FC, label: string, href: string) => {
	return function WrappedIcon() {
		return (
			<TransitionLink href={href}>
				<HoverCard withArrow arrowPosition="center" arrowSize={10}>
					<HoverCardTarget>
						<Container
							key={label}
							size="md"
							p="md"
							w={75}
							className={classes.icon}
						>
							<Icon />
						</Container>
					</HoverCardTarget>
					<HoverCardDropdown>
						<Text>{label}</Text>
					</HoverCardDropdown>
				</HoverCard>
			</TransitionLink>
		)
	}
}

